        var titleGroups = nodeEnter.append("g")
            .classed("title-group", true)
            .attr("transform", "translate(" + -nodeTitleBoxWidth / 3 + ", " + (-nodeTitleBoxHeight / 2 - nodeBodyBoxHeight / 2) + ")");

