            if (!self.getTitleDisplayText.call(self, data))
                return;
            var element = this;
            var selection = d3.select(element);
            var singledOutData = [];
            singledOutData.push(data);

            selection.append("rect")
                .classed("title-box", true)
                .attr("width", nodeTitleBoxWidth)
                .attr("height", nodeTitleBoxHeight);

